import React from "react";
import {
  StatusBar,
  View,
  Text,
  Image,
  StyleSheet,
  Button,
  ScrollView,
  TouchableOpacity
} from "react-native";
import { Colors } from "../contants";
import { Display } from "../utils";
import { useNavigation } from "@react-navigation/native";
import { Dimensions } from "react-native";

export default function RestaurantDetails({ route }) {
  const {
    name,
    main_image,
    rating,
    description,
    menu_images,
    opening_time,
    closing_time,
  } = route.params.restaurant;
  console.log(route.params.restaurant);
  const navigation = useNavigation();

  return (
    <View style={styles.ScreenContainer}>
      <View>
      <Image source={{ uri: main_image }} style={styles.image} />
      </View>
   
    <ScrollView
        showsVerticalScrollIndicator={false}
        contentContainerStyle={styles.ScrollViewFlex}>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            
          }}
        >
    <Text style={styles.name}>{name}</Text>
    <TouchableOpacity  style={styles.menuButton} onPress={() => navigation.navigate("")} ><Text style={styles.menuText}>
      Menu
    </Text>
      </TouchableOpacity>

          
        </View>
        <Text style={styles.description}>{description}</Text> 
        </ScrollView>

        <View style={styles.FooterInfoArea}>
         
          <Text style={styles.category}></Text>
         
        
          

          
         {/* {menu_images.map((menuImage, index) => (
            <Image
              key={index}
              source={{ uri: menuImage }}
              style={styles.Menu}
            />
          ))} */}
        
          <Text style={styles.DescriptionContainer}>{`Rating: ${rating}`}</Text>
          <Text
            style={styles.openingHours}
          >{`Opening Hours: ${opening_time} - ${closing_time}`}</Text>
          <Button title="Go Back" onPress={() => navigation.goBack()} />
          <Button title="Make A reservation " onPress={() => navigation.navigate("ReservationListScreen")} />
     
        </View>
      </View>
     
  );
}

const styles = StyleSheet.create({

  image: {
    width: Dimensions.get("window").width,
    height: 390,
  },
  name: {
    
    fontFamily:"Fakt Pro",
    fontSize: 32,
    fontWeight: "500",
    lineHeight: 40, 
    color:"white",
  },
  category: {
    color: "gray",
  },
  description: {
    color: "gray",
  },

  rating: {
    color: "gray",
  },
  openingHours: {
    color: "gray",
  },
  ScreenContainer: {
    flex: 1,
    backgroundColor:"black"
  
  },
  FooterInfoArea: {
    padding: "20%",
    justifyContent:"center",
  },
  DescriptionContainer: {
   display:"flex",
  flexDirection: 'row',
  justifyContent: 'space-between',
   gap: "20%",
  color:"gray",
  zIndex:"-1",

  },
  CartItemImage: {
    height: "130",
    width: "130",
    borderRadius:"20",

  },
  CartItemImage: {
    height: "130",
    width: "130",
    borderRadius: "20",
    backgroundColor: Colors.DEFAULT_GREEN,
  },
  ScrollViewFlex : {
    paddingHorizontal:20,
    paddingTop:10,

  },
  menuButton :{
    borderRadius: 16,
    backgroundColor: "#F00",
    paddingVertical:8,
    paddingHorizontal:24,

  },
  menuText:{
    color: "#FFF",
textAlign: "center",
fontFamily: "Inter",
fontSize: 16,
fontStyle: "normal",
fontWeight: "700",
lineHeight: 24,
  }
  
});
